mapper.select:
SELECT 

FROM mt_article a

WHERE a.id = #{id}

SELECT
FROM mt_article a
a.del_flag = #{DEL_FLAG_NORMAL}
AND a.title LIKE '%'||#{title}||'%' '%'+#{title}+'%' concat('%',#{title},'%')
ORDER BY ${page.orderBy}
ORDER BY a.update_date DESC

SELECT 

FROM mt_article a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE mt_article SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
